import { useState,useEffect } from "react";
import {handleInitialData, handleInitialData1} from '../actions/shared';
import { connect } from 'react-redux';
import { Navigate } from "react-router-dom";

const Page=(props)=>{
    const{newQuestions,doneQuestions}=props;
    console.log(newQuestions,doneQuestions)
    const{authedUser}=props;
    if(authedUser===null)
        {
            return <Navigate replace to="/" />;
        }
        else{
            
             return(
                <ul>
                    <h1>New Questions</h1>
                {newQuestions.map((doneQuestions)=>(
                    <li key={doneQuestions}>
                        {doneQuestions}
                    </li>
                ))}
                <h1>Done</h1>
                {newQuestions.map((newQuestions)=>(
                    <li key={newQuestions}>
                        {newQuestions}
                    </li>
                ))}

                 </ul>

           
        )}



   
};
const mapStateToProps=({authedUser,questions})=>{
    let keys=Object.keys(questions);
   const newQuestions=keys.filter((key)=>{
    const ques=questions[key];
    return(
        ques.optionOne.votes.includes(authedUser)||ques.optionTwo.votes.includes(authedUser)
    )
   });
   const doneQuestions=keys.filter((key)=>{
    const ques=questions[key];
    return(
        !ques.optionOne.votes.includes(authedUser)&&!ques.optionTwo.votes.includes(authedUser)
    )
   })

    return {
        newQuestions,
        doneQuestions,
        
    }
};
export default connect(mapStateToProps)(Page);
