import { RECEIVE_QUESTIONS } from "../actions/questions";
import { SAVE_ANSWER } from "../actions/questions";
export default function users(state={},action){
    switch(action.type){
        case RECEIVE_QUESTIONS:
            return{...state,
                ...action.questions
            
            };
        case SAVE_ANSWER:
            const{authedUser}=action;
            const{questions,users}=state;
            let us={};
            let ques={};
            us={...users,
                [authedUser]:{
                    ...users[authedUser],
                    answers:{
                        ...users[authedUser].answers,
                        [action.qid]:action.answer,
                    },

                },
            };
            ques={...questions,
                [action.qid]:{
                    ...questions[action.qid],
                    [action.answer]:{
                        ...questions[action.qid][action.answer],
                        votes:questions[action.qid][action.answer].votes.concat([authedUser]),
                    }
                }
            }

            return{
                ...state,
                ...us,
                ...ques,

            }
            default:
                return state;
    }
}
