import { useState,useEffect } from "react";
import {handleInitialData, handleInitialData1} from '../actions/shared';
import { connect } from 'react-redux';
import { Navigate } from "react-router-dom";

const Page=(props)=>{
    const{authedUser}=props;
    if(authedUser===null)
        {
            return <Navigate replace to="/" />;
        }
        else{
            const{newQuestions}=props;
             return(
                <ul>
                {newQuestions.map((newQuestion)=>(
                    <li key={newQuestion}>
                        {newQuestion}
                    </li>
                ))}
                 </ul>

           
        )}



   
};
const mapStateToProps=({authedUser,questions})=>{
    let keyy=Object.keys(questions);
   const newQuestions=keyy.filter((key)=>Object.keys(key.optionOne).filter(k=>k.votes.includes(authedUser))||Object.keys(key.optionTwo).filter(k=>k.votes.includes(authedUser)))

    return {
        newQuestions,
        
    }
};
export default connect(mapStateToProps)(Page);
