import { connect } from 'react-redux';
import { useLocation,useNavigate,useParams } from "react-router-dom";
import { handleSaveAnswer } from '../actions/questions';
const withRouter=(Component)=>{
    const ComponentWithRouterProps=(props)=>{
        let location=useLocation();
        let navigate=useNavigate();
        let params=useParams();
        return <Component {...props} router={{location,navigate,params}}/>
    };
    return ComponentWithRouterProps;
}
const DoneQuestion=(props)=>{
    const{optionOneText,optionTwoText,numOne,numTwo,perOne,perTwo,users}=props;
    const{author}=props.question;
    

    return(
        <div>
            <h1>Poll by {author} </h1>
            <div>
            <li >{optionOneText}</li>
            <li>{numOne}</li>
            <li>{(numOne/(users.length))*100}</li>
            </div>
            <div>
            <li >{optionTwoText}</li>
            <li>{numTwo}</li>
            <li>{numTwo/(users.length)*100}</li>
            </div>
            
            
        </div>
    )
};
const mapStateToProps=({authedUser,questions,users},props)=>{
    const{id}=props.router.params;
    const question=questions[id];
    const optionOneText=question.optionOne['text'];
    const numOne=question.optionOne['votes'].length;
   const perOne =(numOne/(users.length))*100;
    const optionTwoText=question.optionTwo['text'];
    const numTwo=question.optionTwo['votes'].length;
    const perTwo =(numTwo/(users.length))*100;

    return{
        id,
        users,
       question,
       authedUser,
       optionOneText,
       optionTwoText,
       numOne,
       numTwo,
       perOne,
       perTwo,


    }
}
export default withRouter(connect(mapStateToProps)(DoneQuestion));
