import { connect } from 'react-redux';
import { useLocation,useNavigate,useParams } from "react-router-dom";
import { handleSaveAnswer } from '../actions/questions';

const withRouter=(Component)=>{
    const ComponentWithRouterProps=(props)=>{
        let location=useLocation();
        let navigate=useNavigate();
        let params=useParams();
        return <Component {...props} router={{location,navigate,params}}/>
    };
    return ComponentWithRouterProps;
}

const NewQuestion=(props)=>{
    const navigate=useNavigate();
    const {legit}=props;
    if(legit===true){
        console.log("ff")
       //return navigate("/app/error");
    }
    else{
    const{dispatch,authedUser,optionOneText,optionTwoText,id}=props;
    const{author}=props.question;
    const handleAnswer=(e,a)=>{
        e.preventDefault();
        
        dispatch(
            handleSaveAnswer({
                authedUser, 
                qid:id, 
                answer:a,
            })
        );
        navigate("/app");
    }

    return(
        <div>
            <h1>Poll by {author} </h1>
            <li onClick={(e)=>handleAnswer(e,'optionOne')}>{optionOneText}</li>
            <li onClick={(e)=>handleAnswer(e,'optionTwo')}>{optionTwoText}</li>
        </div>
    )
}};
const mapStateToProps=({authedUser,questions},props)=>{
    let legit=true;
    const{id}=props.router.params;
    if(Object.keys(questions).includes(props.router.params)){
        legit=false;
     
    }
   
    const question=questions[id];
    const optionOneText=question.optionOne['text'];
    const optionTwoText=question.optionTwo['text'];

    return{
        legit,
        id,
       question,
       authedUser,
       optionOneText,
       optionTwoText


    }
}
export default withRouter(connect(mapStateToProps)(NewQuestion));
