import { connect } from 'react-redux';
import { useLocation,useNavigate,useParams } from "react-router-dom";
import { handleSaveAnswer } from '../actions/questions';
import { useEffect } from 'react';
import Login from "./Login";
import Error from './Error';
const withRouter=(Component)=>{
    const ComponentWithRouterProps=(props)=>{
        let location=useLocation();
        let navigate=useNavigate();
        let params=useParams();
        return <Component {...props} router={{location,navigate,params}}/>
    };
    return ComponentWithRouterProps;
}

const NewQuestion=(props)=>{
    const navigate=useNavigate();
    const {legit}=props;
    const{authedUser}=props;

    useEffect(() => {
        if (legit === false) {
            console.log("error");
            navigate("/app/error");
           
        }
        if(!authedUser){
            return <Login/>;
          }
    }, []);
    if(legit===true){
    const{dispatch,optionOneText,optionTwoText,id}=props;
    const{author}=props.question;
    const handleAnswer=(e,a)=>{
        e.preventDefault();
        
        dispatch(
            handleSaveAnswer({
                authedUser, 
                qid:id, 
                answer:a,
            })
        );
        navigate("/app");
    }

    return(
        <div>
            <h1>Poll by {author} </h1>
            <li onClick={(e)=>handleAnswer(e,'optionOne')}>{optionOneText}</li>
            <li onClick={(e)=>handleAnswer(e,'optionTwo')}>{optionTwoText}</li>
        </div>
    )}
    else return null;
};
const mapStateToProps=({authedUser,questions},props)=>{
    let legit=true;
    const{id}=props.router.params;
    
    
    if(!Object.keys(questions).includes(id)){
        legit=false;
       
     
    }
    let question = null;
    let optionOneText = '';
    let optionTwoText = '';
    if(legit){question=questions[id];
         optionOneText=question.optionOne['text'];
         optionTwoText=question.optionTwo['text'];}
   
   

    return{
        authedUser,
        legit,
        id, 
       question,
       authedUser,
       optionOneText,
       optionTwoText


    }
}

export default withRouter(connect(mapStateToProps)(NewQuestion));
